			$comparison = $criteria->getComparison(PersonaCastingPeer::ID);
			$value = $criteria->remove(PersonaCastingPeer::ID);
			if ($value) {
				$selectCriteria->add(PersonaCastingPeer::ID, $value, $comparison);
			} else {
				$selectCriteria->setPrimaryTableName(PersonaCastingPeer::TABLE_NAME);
			}

		} else { // $values is PersonaCasting object
			$criteria = $values->buildCriteria(); // gets full criteria
			$selectCriteria = $values->buildPkeyCriteria(); // gets criteria w/ primary key(s)
		}

		// set the correct dbName
		$criteria->setDbName(self::DATABASE_NAME);

		return BasePeer::doUpdate($selectCriteria, $criteria, $con);
	}

