			$objs = array();
		} else {
			$criteria = new Criteria(PersonaCastingPeer::DATABASE_NAME);
			$criteria->add(PersonaCastingPeer::ID, $pks, Criteria::IN);
			$objs = PersonaCastingPeer::doSelect($criteria, $con);
